package COM.JAVA.Day3;

public class increment_decrement_example {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=1; // latest value of i is 1
		i++; // this is example of post increment operator // latest value of i is 2
		++i; // this is an example of pre increment operator //latest value of i is 3
		i--; // example of post decrement // latest value of i is 2
		--i;// example of pre decrement// latest value of i is 1
		
		// what is the latest value of i? 
		System.out.println("The value of i is : " + i);
		
		
		
		
	}

}
