package COM.JAVA.Day3;

public class management_of_spaces {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String student1 = "Tom";
		int age1 = 10;
		int grade1 = 4;
		
		String student2 = "john";
		int age2 = 11;
		int grade2 = 5;
		
		String student3 = "nick";
		int age3 = 12;
		int grade3 = 6;
		
		
		System.out.println("Name of student1 is: " + student1);
		System.out.println("Name of age1 is: " + age1);
		System.out.println("Name of grade1 is: " + grade1);
		System.out.println("\n");
		
		System.out.println("Name of student1 is: " + student2);
		System.out.println("Name of age1 is: " + age2);
		System.out.println("Name of grade1 is: " + grade2);
		System.out.println("\n");
		
		System.out.println("Name of student1 is: " + student3);
		System.out.println("Name of age1 is: " + age3);
		System.out.println("Name of grade1 is: " + grade3);
		
		
		
	}

}
